using UnityEngine; using System.Collections;  public class OverItemDestroyer : MonoBehaviour {      public GameObject MainCamera;            // Use this for initialization  void Start () { 		MainCamera = GameObject.Find("Main Camera");                    }       // Update is called once per frame  void Update () {          //カメラが通り過ぎたアイテムを削除する 		if(this.transform.position.z < MainCamera.transform.position.z){             Destroy(this.gameObject);         }           } }  